function tabla(dato){
    dato = JSON.parse(dato);
    valor = parseFloat(valor) + parseFloat(dato.total);
    msg.payload +=  " </tr>";
    if (dato.precio_promo !== null) msg.payload +=  " <tr align=\"center\" bgcolor= \"#FFFF00\" >";
    if (dato.precio_promo === null) msg.payload +=  " <tr align=\"center\">";
    // msg.payload +=  " <tr align=\"center\">";
    msg.payload +=    " <td>" + dato.cod_interno + "</td>";
    msg.payload +=    " <td>" + dato.descripcion + "</td>";
    // msg.payload +=    " <td>" + dato.gramaje + "</td>";
    msg.payload +=    " <td>" + dato.cantidad + "</td>";    
    msg.payload +=    " <td>" + dato.precio_anterior + "</td>";    
    msg.payload +=    " <td>" + dato.precio + "</td>";
    msg.payload +=    " <td>" + parseFloat(dato.total).toFixed(2) + "</td>";
    msg.payload +=  " </tr>"
}

