function infoCompleta(){
	context.mostrando = 1;
	cont++;
	switch (parseInt(payload)){
		case 1:
			context.pedido = "1";
			largo = largoDatosTemp;
			msg = {pin: 4, payload: " Temp Ambientes "}; node.send(msg);
			if (cont < largoDatosTemp-15){msg = {pin: 5, payload: datosTemp.substring(cont)}; node.send(msg);}
		break;

		case 2:
			context.pedido = "2";
			largo = largoBaterias;
			msg = {pin: 4, payload: "Info de Baterias"}; node.send(msg);
			if (cont < largoBaterias-15){msg = {pin: 5, payload: Baterias.substring(cont)}; node.send(msg);}
		break;

		case 3:
			context.pedido = "3";
			largo = largoToldo;
			msg = {pin: 4, payload: " Info del Toldo "}; node.send(msg);
			if (cont < largoToldo-15){msg = {pin: 5, payload: Toldo.substring(cont)}; node.send(msg);}
		break;
		case 4:
			context.pedido = "4";
			largo = largoCrepuscular;
			msg = {pin: 4, payload: "Info crepuscular"}; node.send(msg);
			if (cont < largoCrepuscular-15){msg = {pin: 5, payload: crepuscular.substring(cont)}; node.send(msg);}
		break;	
		case 5:
			context.pedido = "5";
			largo = largotempMaxMinAyer;
			msg = {pin: 4, payload: "  Temp Max/Min  "}; node.send(msg);
			if (cont < largotempMaxMinAyer-15){msg = {pin: 5, payload: tempMaxMinAyer.substring(cont)}; node.send(msg);}
		break;
		case 6:
			context.pedido = "6";
