function enviarDatos(){
	if (global.get("valvulaTanque") === 1) {context.avisoTanque = 0;}
	msg = {pin: 2, payload: global.get("bombaPozo")}; node.send([null,msg]);
	msg = {pin: 34, payload: global.get("llenarTanque")}; node.send([null,msg]);
	msg = {topic: "USBout", payload: "{\"topic\":\"most\",\"payload\":" + msg.payload + "}"}; node.send(msg);
    msg = {topic: "ESP/Lavadero/Bombas/Estado/Rele1", payload: global.get("bombaPozo")}; node.send(msg);
    msg = {topic: "ESP/Lavadero/Bombas/Estado/Rele0", payload: global.get("valvulaTanque")}; node.send(msg);    
    node.status({text: "Bomba: " + global.get("bombaPozo") + "  Valvula: " + global.get("valvulaTanque") + " - " + global.get("nivTanque") + " - " + global.get("llenarTanque") + " - " + context.estadoAnterior});
