function controlBomba(){
  msg = {topic: "estBomba", payload: global.get("estBomba")}; node.send([msg, null, null]);
  if (global.get("estBomba")=== 1 && context.llenandoTermo === 0){
    context.llenandoTermo = 1;
    global.set("comienzoLlenado", new Date().getTime());}
  if (global.get("estBomba") === 0 && context.llenandoTermo === 1){
    context.llenandoTermo = 0;
    var tiempo = new Date().getTime() - global.get("comienzoLlenado");
    tiempo = parseFloat(tiempo / 1000).toFixed(2);
    global.set("tiempoLlenado", tiempo);
    msg = {topic: "tiempoLlenado", payload: global.get("tiempoLlenado")}; node.send([msg, null, null]);
    enviarBlynk();
  } 
}    

function enviarBlynk(){
    msg = {pin: 101, payload: global.get("autoBomba")}; node.send([null, null, msg]);
    msg = {pin: 102, payload: global.get("estBomba")}; node.send([null, null, msg]);
    msg = {pin: 104, payload: global.get("estResistencia")}; node.send([null, null, msg]);
    msg = {pin: 105, payload: global.get("tiempoLlenado")}; node.send([null, null, msg]);
    msg = {pin: 110, payload: global.get("tempTermoFin") + "  --  " + global.get("tempExterior")}; node.send([null, null, msg]);  
    msg = {pin: 111, payload: global.get("sensorCrep")}; node.send([null, null, msg]);
    msg = {pin: 112, payload: global.get("tempExterior")}; node.send([null, null, msg]);
    msg = {pin: 113, payload: global.get("tempTermoFin")}; node.send([null, null, msg]);
    msg = {pin: 114, payload: global.get("tiempoLlenado")}; node.send([null, null, msg]);
}

function enviarMQTT(){   
    payload = global.get("autoBomba");
    // payload ="0";    
    payload += "#"; payload += global.get("autoResistencia") + "#"; payload += global.get("autoToldo") + "#"; 
    payload += global.get("pedBomba") + "#"; payload += global.get("pedResistencia") + "#"; payload += global.get("pedToldo") + "#"; 
    payload += global.get("tempMaximaTermo") + "#"; payload += global.get("tempMaxResistencia") + "#"; 
    payload += global.get("tempMaxResistenciaManual") + "#"; payload += global.get("tempMinimaResistencia") + "#";
    payload += global.get("valorDia")  + "#"; payload += global.get("valorNoche") + "#" + global.get("segundosMarchaBomba");
    // payload += "#" + global.get("offsetDia")  + "#"; payload += global.get("offsetNoche");
    msg.payload = payload;  
    msg = {topic: "ESP/TermoSolar/Config", payload: payload}; node.send([null, msg, null]);
}

