switch (pin){

  case 8:
    global.set("IslaR", msg.arrayOfValues[0]);
    global.set("IslaG", msg.arrayOfValues[1]);
    global.set("IslaB", msg.arrayOfValues[2]);
    msg = {pin: 32, payload: "12"}; node.send([null, msg]);
    global.set("estIslaRGB", 1);
    enviarMQTT();
  break;

  case 14:
    global.set("estLiving", payload);  
    global.set("estIslaRGB", payload);
    enviarMQTT();
  break;

  case 16:
    global.set("estIsla", payload);
    enviarMQTT();
  break;

  case 17:
    global.set("estCielorrazo", payload); 
    enviarMQTT();
  break;

  case 29:
    global.set("estIslaRGB", payload); 
    enviarMQTT();
  break;

  case 18:
    global.set("itemPotencia", msg.payload);
    if (parseInt(global.get("itemPotencia")) > 0 && parseInt(global.get("itemPotencia")) <= 4){
      context.item = parseInt(msg.payload);
      switch(parseInt(msg.payload)){
          case 1:
              msg = {pin: 19, min: 4, max: 255, label: "Living", payload: global.get("potLiving")};
          break;
          case 2:
              msg = {pin: 19, min: 4, max: 255, label: "Isla", payload: global.get("potIsla")};
          break;
          case 3:
              msg = {pin: 19, min: 4, max: 255, label: "Cielorrazo", payload: global.get("potCielorrazo")};
          break;
          case 4:
              msg = {pin: 19, min: 1, max: 100, label: "Patio Luz", payload: global.get("pwmPatioLuz")};
          break;
      }
      node.send([null, msg]);
    }
  break;

  case 19:
    if (parseInt(global.get("itemPotencia")) > 0 && parseInt(global.get("itemPotencia")) <= 4){
      var valor = parseInt(msg.payload);
      switch(context.item){
          case 1:
              if (global.get("estLiving") === 0)return;
              global.set("potLiving", valor);
              global.set("potIsla", valor);
              global.set("potCielorrazo", valor);
          break;
          case 2:
              if (global.get("estIsla") === 0)return;
              global.set("potIsla", valor);
          break;
          case 3:
              if (global.get("estCielorrazo") === 0)return;
              global.set("potCielorrazo", valor);
          break;
          case 4:
              if (global.get("Dia") === 1)return;
              global.set("pwmPatioLuz", valor);
              msg = {topic: "USBout", payload: "Noche" + global.get("pwmPatioLuz")}; node.send([msg, null]); return;
      }
      enviarMQTT();
    }
  break;
}
