function enviarMQTT(){
    var topicNuevo = msg.topic;
    //msg = {pin: 127, payload: global.get("hsCpu") + ", top: " + topicNuevo + ", D:" + global.get("Dia") + ", Al:" + global.get("estadoAlarma") + "\n"}; node.send([null, msg]);
    // if (global.get("estIsla") === 0 && global.get("estCielorrazo") === 0 && global.get("estIslaRGB") === 0)global.set("estLiving", 0);
    if (global.get("estIsla") === 1 || global.get("estCielorrazo") === 1){
        if(topic !== "Blink/estLiving" && topicNuevo !== "fallas_MQTT")
          if (payload !== 0) global.set("estLiving", 1);
        }
    if (topicNuevo == "apagadoAuto") {
        global.set("estLiving", 0); 
        msg = {pin: 127, payload: "Apagado auto\n"}; node.send([null, msg]);
    }

    if (topicNuevo !== "fallas_MQTT"){
    msg = {pin: 14, payload: global.get("estLiving")}; node.send([null, msg]);
    msg = {pin: 16, payload: global.get("estIsla")}; node.send([null, msg]);
    msg = {pin: 17, payload: global.get("estCielorrazo")}; node.send([null, msg]);
    msg = {pin: 29, payload: global.get("estIslaRGB")}; node.send([null, msg]);}
    if(topic == "ESP/living/Iluminacion/Envio")return;

    if (parseInt(global.get("estLiving")) === 0) {
            msg = {pin: 16, payload: "0"}; node.send([null, msg]);
            dato = "0,"}
        else{
            dato = global.get("estIsla") + ",";}
    dato += global.get("potIsla") + "#";

    if (parseInt(global.get("estLiving")) === 0) {
            msg = {pin: 17, payload: "0"}; node.send([null, msg]);
            dato += "0,"}
        else{
            dato += global.get("estCielorrazo") + ",";}
    dato += global.get("potCielorrazo") + "#";  
    dato += global.get("estIslaRGB") + ",";
    dato += global.get("IslaR") + ",";
    dato += global.get("IslaG") + ",";      
    dato += global.get("IslaB") + "#";

  dato += global.get("pwmMaxLed") + "#";
  dato += global.get("pwmMinLed") + "#";
    // dato += global.get("dimmerLeds") + "#";

    dato += global.get("minTeclas") + "#";
    dato += global.get("maxTeclas") + "#";
    dato += global.get("doblePuls");
    dato += "#" + global.get("Dia");
    if (topicNuevo == "crepMQTT")dato += "#";

//  msg = {topic: "ESP/living/Iluminacion/Recibo2", payload: topicNuevo + "&" + global.get("estadoAlarma") + "&" + global.get("Dia") + "\n" + dato};    node.send([msg, null]);
    msg = {topic: "ESP/living/Iluminacion/Recibo", payload: dato};
    node.send([msg, null]);     
}

