function comprobarToldo(){
switch (dato.payload){
	case 0:
		global.set("estadoToldo", "Parado");		
		context.estadoToldo = 0;	
		if (context.error !== 0)global.set("estadoToldoLCD", "Toldo parado de forma manual (Blynk)");
		msg = {topic: "INSERT INTO \`toldo\`(\`fecha\`, \`estado\`, \`tiempoMov\`) VALUES (now(),\"" + global.get("estadoToldoLCD") + "\"," + global.get("tiempoMoviendo") + ")"};		
		msg = {topic: "Blynk/actualizar/estado", payload: 1}; node.send([msg, null, null]);
		if (context.estAnt === 0){msg = {payload: 0, color: "#ffbc26", label: "Toldo Parado", offlabel: "Abrir", pin: 3}; node.send([null, msg, null]); return;}
		if (context.estAnt === 1){msg = {payload: 0, color: "#ffbc26", label: "Toldo Parado", offlabel: "Cerrar", pin: 3}; node.send([null, msg, null]); return;}
	break;
	case 1:	
	    // if (global.get("estadoToldo") === "Abierto") return;
		context.AbrirToldoAuto = 1;
		context.error = 0;
		if (global.get("estadoToldo") === "Cerrado" || global.get("estadoToldo") === "Error Toldo abriendo" || global.get("estadoToldo") === "Error Toldo cerrando")global.set("tiempoMoviendo", 0);
		msg = {payload: 1, color: "#ffbc26", label: "Tol. Abriendo", onlabel: "Parar", pin: 3}; node.send([null, msg, null]);
		context.estadoToldo = 1;
		context.estAnt = 1;
		global.set("estadoToldo", "Abriendo");
		node.status({text: global.get("estadoToldo") + " " + context.tiempoToldo});//toldo abierto
	break;
	case 2:
		msg = {topic: "USBout", payload: "{\"topic\":\"abiertoOK\",\"payload\":0}"}; node.send([msg, null, null]);
		if (global.get("estadoToldo") == "Abierto")return;
		global.set("estadoToldo", "Abierto");		
		global.set("toldoManual", 0);
		if (context.condicionEstadoToldo === 0)global.set("estadoToldoLCD", "Toldo Abierto de forma manual (Blynk)" + global.get("hsCpu"));
		if (context.condicionEstadoToldo === 1)global.set("estadoToldoLCD", "Toldo Abierto por temp (" + global.get("hsCpu") + ", temp: " + global.get("tempPatioLuz") + ")");
		if (context.condicionEstadoToldo === 2)global.set("estadoToldoLCD", "Toldo Abierto por Oscurecer("  + global.get("hsCpu") + ")");
		if (context.condicionEstadoToldo === 3)global.set("estadoToldoLCD", "Toldo Abierto por Amanecer("  + global.get("hsCpu") + ")");
		var moviendo = Math.abs(tiempoMover - global.get("tiempoMoviendo"));
		msg = {topic: "INSERT INTO \`toldo\`(\`fecha\`, \`estado\`, \`tiempoMov\`, \`tempPatioLuz\`) VALUES (now(),\"" + global.get("estadoToldoLCD") + "\"," + moviendo + "," + global.get("tempPatioLuz") + ")"};
		node.send([null, null, msg]);	
		msg = {pin: 3, payload: 1, color: "#23C48E", label: "Toldo Abierto", onlabel: "Cerrar"}; node.send([null, msg, null]);
		msg = {topic: "notifyBlynk", payload: "Toldo Abierto"}; node.send([msg, null, null]);
		context.estadoToldo = 2;
		context.estAnt = 1;
		msg = {topic: "Blynk/actualizar/estado", payload: 1}; node.send([msg, null, null]);
		return;
	case 3:
		// if (global.get("estadoToldo") === "Cerrado") return;		
		context.CerrarToldoAuto = 1;	
		context.error = 0;
		if (global.get("estadoToldo") === "Abierto" || global.get("estadoToldo") === "Error Toldo abriendo" || global.get("estadoToldo") === "Error Toldo cerrando")global.set("tiempoMoviendo", 0);
		msg = {payload: 1, color: "#ffbc26", label: "Tol. Cerrando", onlabel: "Parar", pin: 3}; node.send([null, msg, null]);
		context.estadoToldo = 3;
		context.estAnt = 0;
		global.set("estadoToldo", "Cerrando");
		node.status({text: global.get("estadoToldo") + " " + context.tiempoToldo});
	break;
