function controlBomba(){
	if (global.get("estBomba") === 1 && context.llenandoTermo == 0){
		context.llenandoTermo = 1;
		global.set("comienzoLlenado", new Date().getTime());}
	if (global.get("estBomba") === 0 && context.llenandoTermo === 1){
		context.llenandoTermo = 0;
		var tiempo = new Date().getTime() - global.get("comienzoLlenado");
		tiempo = parseFloat(tiempo / 1000).toFixed(2);
		global.set("tiempoLlenado", tiempo);
		msg = {topic: "tiempoLlenado", payload: global.get("tiempoLlenado")}; node.send([msg, null, null, null]);
