function enviarDatos(){   
	msg = {pin: 101, payload: global.get("autoBomba")}; node.send([null, null, msg, null]);
	msg = {pin: 102, payload: global.get("pedBomba")}; node.send([null, null, msg, null]);
	msg = {pin: 104, payload: global.get("pedResistencia")}; node.send([null, null, msg, null]);
